import React, { useState } from "react";
import { jsPDF } from "jspdf";

const ResultDisplay = ({ result }) => {
  const [format, setFormat] = useState("txt");

  const speakSummary = () => {
    const utterance = new SpeechSynthesisUtterance(result.summary);
    utterance.lang = "en-US";
    utterance.rate = 1;
    utterance.pitch = 1;
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(utterance);
  };

  const downloadFile = () => {
    const content = `
--- Cleaned Transcript ---
${result.cleaned_text}

--- Summary (${format.toUpperCase()}) ---
${result.summary}
`;

    const filename = `subtitle_summary_${new Date().toISOString().slice(0, 19).replace(/:/g, "-")}.${format}`;

    if (format === "txt") {
      const blob = new Blob([content], { type: "text/plain;charset=utf-8" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = filename;
      link.click();
    } else if (format === "pdf") {
      const doc = new jsPDF();
      const lines = doc.splitTextToSize(content, 180);
      doc.text(lines, 10, 10);
      doc.save(filename);
    }
  };

  return (
    <div style={{
      background: "#fff",
      borderRadius: "12px",
      padding: "2rem",
      marginTop: "2rem",
      boxShadow: "0 0 15px rgba(0,0,0,0.1)"
    }}>
      <h2>üìù <strong>Cleaned Transcript</strong></h2>
      <p style={{ marginBottom: "2rem", lineHeight: "1.6" }}>{result.cleaned_text}</p>

      <h2>üß† <strong>Summary</strong></h2>
      <p style={{ marginBottom: "1rem", lineHeight: "1.6" }}>{result.summary}</p>

      <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginTop: "1rem" }}>
        <button
          onClick={speakSummary}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer"
          }}
        >
          üîä Play Summary
        </button>

        <select
          value={format}
          onChange={(e) => setFormat(e.target.value)}
          style={{
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc"
          }}
        >
          <option value="txt">Download as .txt</option>
          <option value="pdf">Download as .pdf</option>
        </select>

        <button
          onClick={downloadFile}
          style={{
            padding: "10px 20px",
            backgroundColor: "#28a745",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer"
          }}
        >
          ‚¨áÔ∏è Download
        </button>
      </div>
    </div>
  );
};

export default ResultDisplay;
