import React from "react";

const ResultDisplay = ({ result }) => {
  const speakSummary = () => {
    const utterance = new SpeechSynthesisUtterance(result.summary);
    utterance.lang = "en-US";
    utterance.rate = 1;
    utterance.pitch = 1;
    window.speechSynthesis.cancel(); // Stop any ongoing
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div style={{
      background: "#fff",
      borderRadius: "12px",
      padding: "2rem",
      marginTop: "2rem",
      boxShadow: "0 0 15px rgba(0,0,0,0.1)"
    }}>
      <h2>üìù <span style={{ fontWeight: "bold" }}>Cleaned Transcript</span></h2>
      <p style={{ marginBottom: "2rem", lineHeight: "1.6" }}>{result.cleaned_text}</p>

      <h2>üß† <span style={{ fontWeight: "bold" }}>Summary</span></h2>
      <p style={{ marginBottom: "1rem", lineHeight: "1.6" }}>{result.summary}</p>

      <button
        onClick={speakSummary}
        style={{
          padding: "10px 20px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer"
        }}
      >
        üîä Play Summary
      </button>
    </div>
  );
};

export default ResultDisplay;
